const express = require('express');
const cors = require('cors');
const path = require('path');
const app = express();

app.use(express.json());
app.use(cors());

// --------------------------------------------------------------------

// Solo Randomizer
let teams = [
    {
        id: 'sunshine',
        name: `"Ain't No Sunshine"`,
        imgUrl: "data:image/jpeg;base64,",
        site: "https://www.youtube.com/watch?v=ktyhXq00RxQ",
        tier: 3
    },
    {
        id: 'vultures',
        name: `"Vultures"`,
        imgUrl: "data:image/jpeg;base64,",
        site: "https://www.youtube.com/watch?v=vKU9fWdpxQA",
        tier: 4
    },
    {
        id: 'voodoo',
        name: `"Voodoo Child"`,
        imgUrl: "data:image/jpeg;base64,",
        site: "https://www.youtube.com/watch?v=p7-orgHG7CU",
        tier: 5
    }
]

const values = Object.values(teams)

app.get("/api/teams", (req, res)=>{
    const randomTeam = values[Math.floor(Math.random()*values.length)]
    // console.log(randomTeam)
    res.status(200).send(randomTeam)
})

